    ComposableMap,
    Geographies,
    Geography,
    Marker,
    Annotation,
} from 'react-simple-maps';
import LocationCard from "./LocationCard";
const geoUrl = 'https://cdn.jsdelivr.net/npm/us-atlas@3/states-10m.json';


function Vaccination() {
    // const [content, setContent] = useState(null);
